$(document).ready(function(){$(function(){introguide=introJs();introguide.setOptions({steps:[{element:'.search_div_form',intro:'Search for latest jobs by skill, location, experience',position:'bottom'},{element:'.user_profile',intro:'Complete your profile with all your Personal and professional info. So, you can catch the Recruiters attention.',position:'bottom'},{element:'.user_alerts',intro:'Get all relevant jobs to your inbox as per your requirement by creating alerts',position:'bottom'},{element:'#user_calendar',intro:'Easy to track all your events or jobs on everyday',position:'bottom'},{element:'#resume_upload_block',intro:'Upload your resume to get attention of Recruiters',position:'bottom'},{element:'#subscribe_blockk',intro:'Subscribe with your interested skills to get job notifications',position:'bottom'},]})
introguide.start();introguide.oncomplete(function(){window.location="/profile/"});})
$("select#job_skills").select2({placeholder:"Select here",maximumSelectionSize:100});$('#job_skills').empty()
$('#job_skills').append(new Option('Select a Skill',''))
$.getJSON("/jobs/get_skills/",function(data){$.each(JSON.parse(data.response),function(key,field){$('#job_skills').append(new Option(field.fields.name,field.pk))})});});