$(".cancelbutton").click(function(e){window.location="/profile/";});$("form#editemailform").submit(function(e){e.preventDefault();$.post(".",$("form#editemailform").serialize(),function(data){if(data.error){$('p.hint').remove();for(var key in data.response){$('#'+key).after('<p class="hint">'+data.response[key]+'</p>');}}else{open_dialog_with_url('Your profile has been updated successfully','Success!!!',"/profile/")}},'json');});;